(88, 'Smart Tivi LG 4K 55 inch 55SM8600PTA', 18000000, 50, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công nghệ&nbsp;<i class=\"cate-img\"></i>NanoCell 2&nbsp;cho bạn chất lượng hình ảnh vượt trội và trung thực gần với thực tế.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ phân giải&nbsp;<i class=\"cate-yt\"></i>4K&nbsp;cực nét cùng công nghệ&nbsp;<i class=\"cate-img\"></i>4K Cinema HDR&nbsp;giúp hình ảnh trở nên chân thực, sống động.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Nâng cấp những hình ảnh nguồn vào lên gần chuẩn 4K nhờ công nghệ&nbsp;4K Upscaler.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tạo ra ranh giới rõ rệt giữa luồng sáng và tối nhờ công nghệ&nbsp;<i class=\"cate-img\"></i>Ultra Luminance.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hỗ trợ chiếu màn hình điện thoại lên tivi và điều khiển tivi bằng điện thoại qua ứng dụng&nbsp;<i class=\"cate-yt\"></i>LG TV Plus.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công nghệ âm thanh vòm kỹ thuật số&nbsp;<i class=\"cate-img\"></i>DTS Virtual:X</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tối ưu hình ảnh, âm thanh theo môi trường và nội dung đang phát bằng trí thông minh nhân tạo AI nhờ bộ xử lý&nbsp;<i class=\"cate-img\"></i>Alpha 7 thế hệ thứ 2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><i class=\"cate-img\"></i>Hệ điều hành WebOS&nbsp;4.5 dễ sử dụng, đi kèm remote thông minh hỗ trợ&nbsp;<i class=\"cate-yt\"></i>tìm kiếm giọng nói bằng tiếng Việt&nbsp;cả 3 miền.</span></li>\n</ul>', 1, 1, 2, '2021-01-19 07:22:40', NULL, NULL, NULL),
(89, 'Smart Tivi LG 4K 43 inch 43UM7300PTA', 7750000, 25, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chân đế dạng chữ V, gồm 2 chân có thiết kế thanh mảnh đẹp và chắc chắn</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Mang đến độ phân giải 4K nét gấp 4 lần tivi Full HD</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công nghệ 4K Active HDR cho hình ảnh cực kì chi tiết</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">công nghệ True Color Accuracy mang đến cho người xem những trải nghiệm hình ảnh đầy màu sắc</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">có khả năng loại bỏ nhiễu và tạo ra màu sắc và độ tương phản sinh động hơn</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">công nghệ 4K Upscaler giúp nâng cấp các nội dung hình ảnh</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">mang đến những âm thanh vòm đa chiều vô cùng mạnh mẽ</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">sở hữu kho ứng dụng đa dạng:&nbsp;Trình duyệt web, YouTube, Netflix, Zing TV, VnExpress, Film+,&nbsp;The Karaoke Chanel</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hỗ trợ tìm kiếm bằng giọng nói</span></li>\n</ul>', 1, 1, 2, '2021-01-19 07:22:41', NULL, NULL, NULL);
