(603, 'Quạt điều hòa Midea AC100-U', 2541000, 50, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt Midea&nbsp;có tặng kèm<strong>&nbsp;2&nbsp;hộp đá khô.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt&nbsp;có công suất 50 W, làm mát, tạo hơi ẩm nhanh cho diện tích 10 – 15 m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chức năng<strong>&nbsp;tạo ion</strong>&nbsp;lọc sạch, tạo sự thoải mái cho bầu không khí.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tấm làm mát lọc sạch bụi bẩn.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa 7 lít, sử dụng 4 -5 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tính năng tự ngắt bơm khi cạn, tránh hư hại sản phẩm.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dễ dùng với&nbsp;<strong>bảng điều khiển nút nhấn đơn giản</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hẹn giờ tắt&nbsp;đến 7 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Điều khiển từ xa&nbsp;tiện dụng</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Midea và sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 10, '2021-01-19 10:19:41', NULL, NULL, NULL),
(604, 'Quạt điều hoà Daikiosan DKA-04500D', 5330000, 50, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Công suất 200 W&nbsp;mạnh mẽ làm mát&nbsp;</strong>25 – 30 m2&nbsp;.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn<strong>&nbsp;nhỏ hơn 55 dB.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><b>Tấm làm mát&nbsp;</b>làm từ gỗ, hiệu quả làm mát cao.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích&nbsp;<strong>36 lít</strong>, dùng đến&nbsp;<strong>10 tiếng.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Bảng điều khiển nút nhấn</strong>&nbsp;điện tử nhạy bén, hiện đại,&nbsp;<strong>kèm điều khiển từ xa</strong>&nbsp;tiện lợi.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><b>T</b>hương hiệu Daikiosan của&nbsp;<strong>Đại Việt</strong>&nbsp;–&nbsp;<strong>Việt Nam</strong>, sản xuất tại<strong>&nbsp;Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 19, '2021-01-19 10:19:44', NULL, NULL, NULL),
(605, 'Quạt điều hòa Midea AC200-17JR', 3908000, 10, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tặng kèm 2&nbsp;<strong>hộp đá khô</strong>&nbsp;làm tăng độ mát cho phòng ở.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hoạt động với công suất 200 W, lưu lượng gió 310 m3/h, phạm vi làm mát rộng từ 25 – 30 m².</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn dưới 65dB tương đương tiếng văn phòng làm việc, sảnh yên tĩnh của khách sạn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích 50 lít, kèm thang đo hiển thị mực nước.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tính năng tự ngắt bơm khi bình cạn nước&nbsp;<strong>tăng độ bền cho sản phẩm tối đa.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển nút nhấn kèm&nbsp;<strong>màn hình hiển thị</strong>&nbsp;có chỉ dẫn dễ hiểu, dễ sử dụng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chức năng&nbsp;hẹn giờ tắt&nbsp;tiện dụng với&nbsp;<strong>thời gian hẹn tối đa 8 tiếng.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Máy có&nbsp;điều khiển từ xa nhỏ gọn, tiện thao tác.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Midea, sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 10, '2021-01-19 10:19:46', NULL, NULL, NULL),
(606, 'Quạt điều hoà Kangaroo KG50F66', 3742000, 35, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tặng kèm&nbsp;<strong>2 cục đá khô</strong>&nbsp;cho độ mát tốt hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Công suất mạnh mẽ 180 W</strong>, quạt điều hòa có phạm vi làm mát từ 45 – 50 m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tấm làm mát<strong>&nbsp;bằng gỗ sồi</strong>, độ bền cao, làm mát hiệu quả.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích<strong>&nbsp;45 lít</strong>, thời gian sử dụng khoảng từ 10 – 15 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Điều khiển&nbsp;<strong>núm vặn</strong>&nbsp;dễ tùy chỉnh 3 tốc độ gió.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thương hiệu Kangaroo – <strong>Việt Nam</strong>, sản xuất tại&nbsp;<strong>Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 21, '2021-01-19 10:19:48', NULL, NULL, NULL),
(607, 'Quạt điều hòa Kangaroo KG50F68', 4666000, 40, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Đá<strong>&nbsp;</strong>khô&nbsp;đi kèm giúp làm mát nhanh hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa công suất 200 W làm mát tốt trong phạm vi từ 20 – 25 m².</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn 50 – 60 dB.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chế độ tạo ion lọc không khí&nbsp;<strong>loại bỏ bụi bẩn, vi khuẩn, cho không khí sạch sẽ hơn.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hệ thống<strong>&nbsp;làm lạnh bằng chip điện tử</strong>, làm mát hiệu quả hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tấm lọc bụi thô bên ngoài</strong>&nbsp;và&nbsp;<strong>1 tấm làm mát</strong>&nbsp;bên trong giúp lọc sạch không khí.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa nước có&nbsp;<strong>dung tích 11 lít</strong>, có thể tháo rời,&nbsp;<strong>thang đo mực nước quan sát</strong>, châm nước dễ dàng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tự ngắt máy bơm khi cạn nước</strong>, hẹn giờ tắt linh hoạt.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Bàng điều khiển cảm ứng kèm màn hình hiển thị rõ nét</strong>,&nbsp;<strong>remote từ xa&nbsp;</strong>tiện dụng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thương hiệu Kangaroo – Việt Nam, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 21, '2021-01-19 10:19:51', NULL, NULL, NULL),
(608, 'Quạt điều hoà Kangaroo KG50F62', 2250000, 25, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tặng kèm<strong>&nbsp;hộp đá khô làm mát giúp giảm nhiệt độ tốt hơn.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa&nbsp;công suất 100 W, tạo lưu lượng gió 3700 m3/h,&nbsp;<strong>làm mát trên diện tích 25 – 30 m2</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích bình chứa nước 30 lít,&nbsp;<strong>sử dụng liên tục 10 – 15 tiếng</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hoạt động bền tốt với chức năng tự ngắt bơm khi quá nhiệt.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển nút nhấn và núm xoay đơn giản.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thương hiệu Kangaroo – Việt Nam, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 21, '2021-01-19 10:19:53', NULL, NULL, NULL),
(609, 'Quạt điều hoà Daikiosan DKA-04000B', 4000000, 25, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa có&nbsp;<strong>công suất đạt 160 W</strong>, tạo lưu lượng gió 4000 m3/h, diện tích làm mát từ 25 – 30 m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn dưới 62dB tương đương âm thanh sảnh khách sạn yên tĩnh.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa 40 lít, sử dụng 8-10 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Kèm thang đo mực nước dễ dàng quan sát.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">An toàn khi dùng tính năng tự ngắt bơm khi bình cạn nước.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển nút nhấn, hiển thị rõ nét.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Trang bị remote dễ dàng điều khiển ở vị trí cách xa quạt.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><b>T</b>hương hiệu Đại Việt uy tín của Việt Nam, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 19, '2021-01-19 10:19:56', NULL, NULL, NULL),
(610, 'Quạt điều hòa Kangaroo KG50F72', 3741000, 5, '<ul>\n <li><strong>2 hộp đá khô</strong>&nbsp;đi kèm giúp làm mát nhanh chóng.</li>\n <li>Công suất 180 W phù hợp làm mát diện tích&nbsp;30 – 40 m2.</li>\n <li>Độ ồn dưới mức 63 dB tương đương tiếng văn phòng làm việc.</li>\n <li>Tấm làm mát bằng gỗ sồi ép thân thiện với môi trường và làm mát hiệu quả.</li>\n <li>Dung tích 55 lít,<strong>&nbsp;sử dụng liên tục hơn 15 tiếng</strong>, không phải châm nước nhiều lần.</li>\n <li><strong>Tự ngắt khi cạn nước, giúp bảo vệ động cơ và gia tăng tính an toàn.</strong></li>\n <li>Bảng điều khiển cơ: nút nhấn đơn giản, dễ dàng thao tác.</li>\n <li>Thương hiệu Kangaroo của Việt Nam – sản xuất tại Trung Quốc.</li>\n</ul>', 1, 5, 21, '2021-01-19 10:19:58', NULL, NULL, NULL),
(611, 'Quạt điều hòa Kangaroo KG50F20', 3167000, 5, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt công suất&nbsp;<strong>110 W,</strong>&nbsp;tạo làn hơi nước mạnh mẽ làm mát không khí hiệu quả.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Độ ồn 40 – 60 dB.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Chế độ ion lọc sạch không khí</strong>, đáp ứng nhu cầu làm mát và mang đến cho gia đình bạn bầu không khí trong sạch hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tấm lưới lọc bụi thô bên ngoài tấm làm mát t<strong>ăng khả năng lọc không khí và làm mát hiệu quả.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa nước dung tích&nbsp;<strong>20 lít</strong>, có thể tách rời khỏi thân máy giúp việc vệ sinh bình dễ dàng hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển&nbsp;<strong>cảm ứng</strong>&nbsp;hiện đại,<strong>&nbsp;remote</strong>&nbsp;điều khiển từ xa, giúp bạn không phải đi lại nhiều lần.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thương hiệu <strong>Kangaroo – Việt Nam</strong>, sản xuất tại&nbsp;<strong>Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 21, '2021-01-19 10:20:00', NULL, NULL, NULL),
(612, 'Quạt điều hòa Midea AC200-19WV', 3241000, 0, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tặng kèm<strong>&nbsp;2 cục đá khô&nbsp;</strong>giúp giảm nhiệt độ đáng kể.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất 100 W, phù hợp diện tích 20 – 30 m².</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Có độ ồn nhỏ hơn 62dB.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chức năng tạo i<strong>on lọc sạch không khí</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tấm làm mát bằng&nbsp;<strong>sợi thực vật</strong>, rất tốt cho quá trình hút nước, lọc không khí.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích&nbsp;<strong>7 lít có thể sử dụng từ 5 – 6 tiếng.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển cảm ứng dễ dàng thao tác, kèm remote từ xa đa chức năng, hẹn giờ tắt 12 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Midea – Trung Quốc, sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 10, '2021-01-19 10:20:06', NULL, NULL, NULL),
(613, 'Quạt điều hòa Kangaroo KG50F07', 4096000, 30, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tặng<strong>&nbsp;2 hộp đá khô</strong>&nbsp;cho không khí mát mẻ, dễ chịu hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa công suất 55 W,&nbsp;diện tích làm mát 15 – 20 m².</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn 45dB – 60dB tương đương tiếng thì thầm hay tiếng văn phòng làm việc.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Chế độ làm mát đa chiều.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Màng lọc bụi bẩn, bảo vệ sức khỏe.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích 10 lít. Kèm thang đo&nbsp;<strong>mực nước tiện quan sát.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tính năng tự động ngắt bơm khi bình cạn nước tăng tuổi thọ cho sản phẩm.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển bằng nút nhấn điện tử hiện đại, tiện thao tác.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Chế độ hẹn giờ đến 7 tiếng</strong>, tiện lợi vào ban đêm.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Điều khiển từ xa tiện lợi, không cần lại gần quạt.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Kangaroo của Việt Nam – Sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 21, '2021-01-19 10:20:11', NULL, NULL, NULL),
(614, 'Quạt điều hoà Gree KSWK-2001DegL', 3463000, 10, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa làm mát lạnh với<strong>&nbsp;4 viên đá khô tặng kèm.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất 145 W&nbsp;mạnh mẽ làm mát&nbsp;25 – 30m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn<strong>&nbsp;nhỏ hơn 73 dB&nbsp;</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chế độ tạo&nbsp;<strong>Ion âm loại bỏ bụi bẩn, vi khuẩn.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tăng tuổi thọ cho sản phẩm&nbsp;với chức năng tự ngắt bơm khi cạn nước.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển cảm ứng, hiện đại, kèm điều khiển từ xa tiện lợi.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Gree – sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 20, '2021-01-19 10:20:15', NULL, NULL, NULL);
