(630, 'Quạt điều hòa Midea AC375-19CRH', 4583000, 10, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">2 viên đá khô đi kèm,&nbsp;<strong>tăng khả năng làm mát.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất&nbsp;<strong>130 W</strong>, làm mát trên&nbsp;<strong>diện tích 20 – 30 m2.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Độ ồn 63 dB</strong>, không gây chói tai khi vận hành.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tấm làm mát bằng sợi thực vật,&nbsp;<strong>làm mát hiệu quả</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa nước<strong>&nbsp;lớn đến 45 lít</strong>, có thang đo mực nước tiện lợi.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hẹn giờ tắt lên đến<strong>&nbsp;24 tiếng</strong>, tự ngắt bơm khi cạn nước.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dễ dùng với&nbsp;<strong>điều khiển nút nhấn</strong>, 3 mức gió tùy chọn, có đèn LED hiển thị.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Remote điều khiển từ xa,&nbsp;<strong>không cần phải lại gần.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><b>T</b>hương hiệu Midea – Trung Quốc, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 10, '2021-01-19 10:20:52', NULL, NULL, NULL),
(631, 'Quạt cây Toshiba F-LSD30(W)VN', 1916000, 25, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt công suất 30 W, trang bị động cơ&nbsp;<strong>inverter tiết kiệm điện</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">9 cánh quạt đường kính 40 cm mang lại làn gió mát, trải rộng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>26 mức tốc độ gió</strong>, cài đặt hẹn giờ mở, tắt đến 12 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt có thể điều chỉnh độ cao thấp từ 122 – 133 cm phù hợp nhiều không gian sử dụng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Nhật Bản</strong>, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 16, '2021-01-19 10:20:55', NULL, NULL, NULL),
(632, 'Quạt điều hoà Gree KSWK-10X61D', 3075000, 35, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tặng kèm 2 hộp&nbsp;<strong>đá khô</strong>&nbsp;giúp độ làm mát tốt hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất 68W, phù hợp không gian 15 – 20 m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Độ ồn&nbsp;nhỏ hơn 64dB</strong>,&nbsp;tương đương tiếng văn phòng làm việc, sảnh yên tĩnh.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Không khí trong lành, tinh khiết với&nbsp;<strong>chức năng tạo Ion.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tấm&nbsp;<strong>làm mát bằng sợi thực vật</strong>, hiệu quả làm mát cao, thân thiện với môi trường.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích bình chứa 10 lít, sử dụng 8-9 tiếng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tự ngắt khi bơm cạn nước giúp kéo dài tuổi thọ sản phẩm.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Hẹn giờ bật, tắt quạt lên đến&nbsp;<strong>15 tiếng,</strong>&nbsp;vô cùng tiện lợi.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Điều khiển từ xa, không cần lại gần.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thương hiệu Gree, sản xuất tại Trung Quốc</span></li>\n</ul>', 1, 5, 20, '2021-01-19 10:20:58', NULL, NULL, NULL),
(633, 'Quạt đứng Toshiba F-LSD10(W)VN', 1500000, 30, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt có đến 9 cánh, công suất 30 W, làm mát hiệu quả.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thiết lập 4 chế độ gió, 26 mức tốc độ gió, hẹn giờ tắt 12 giờ tiện dụng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chức năng Inverter tiết kiệm tới 70% điện năng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chiều cao thay đổi linh hoạt từ 113 – 133 cm tùy theo nhu cầu sử dụng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu uy tín&nbsp;Toshiba&nbsp;của Nhật Bản</strong>, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 16, '2021-01-19 10:25:02', NULL, NULL, NULL),
(634, 'Quạt đứng Toshiba F-LSA20(H)VN', 1250000, 0, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất 60 W kết hợp 5 cánh quạt cho gió mát, thổi xa hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Remote đi kèm</strong>&nbsp;tiện điều chỉnh chế độ quạt từ xa dễ dàng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Chế độ hẹn giờ tối đa 7.5 giờ</strong>, thuận tiện với giấc ngủ về đêm.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Có thể thay đổi chiều cao từ&nbsp;<strong>113 – 133 cm</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu uy tín Nhật Bản – Toshiba</strong>, sản xuất tại Trung Quốc, được nhiều người tin dùng.</span></li>\n</ul>', 1, 5, 16, '2021-01-19 10:25:10', NULL, NULL, NULL),
(635, 'Quạt đứng Toshiba F-LSA10(W)VN Trắng', 750000, 5, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt đứng công suất 50 W mạnh mẽ, làm mát không gian rộng với 5 cánh quạt sải 40 cm.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chế độ&nbsp;<strong>hẹn giờ tắt quạt</strong>&nbsp;kết hợp&nbsp;<strong>bảng điều khiển dễ sử dụng</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Khởi động nhanh</strong>&nbsp;nhờ động cơ bạc thau.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chiều cao thay đổi linh hoạt từ 114 – 133.5 cm tùy theo nhu cầu dùng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Toshiba – Nhật Bản</strong>, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 16, '2021-01-19 10:25:13', NULL, NULL, NULL),
(636, 'Quạt điều hòa Sunhouse SHD7718', 3691000, 0, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>2 hộp đá khô</strong>&nbsp;đi kèm, giúp tỏa hơi nước mát hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa&nbsp;công suất 120 W, diện tích làm mát 20 – 25 m².</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn từ 40dB – 60dB tương đương tiếng thì thầm hay tiếng văn phòng làm việc, sảnh yên tĩnh của khách sạn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tạo Ion âm</strong>, giúp không khí sạch thoáng hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chức năng&nbsp;<strong>bắt muỗi hiệu quả.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tấm làm mát lọc sạch bụi bẩn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích 13 lít, thang đo mực nước tiện quan sát.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển bằng nút nhấn điện tử,&nbsp;<strong>đèn LED hiển thị.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Remote điều khiển từ xa tiện lợi.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Sunhouse – Việt Nam, sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 22, '2021-01-19 10:25:16', NULL, NULL, NULL),
(637, 'Quạt điều hòa Sunhouse SHD7727', 3991000, 20, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tặng kèm 2 viên đá khô</strong>&nbsp;tăng khả năng làm mát cho không khí.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất 150 W, làm mát diện tích 25 – 30 m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn 55dB – 65dB tương đương âm thanh rạp chiếu phim hay văn phòng làm việc, sảnh yên tĩnh của khách sạn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Chế độ đảo gió 2 chiều trái – phải.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tạo ion lọc sạch không khí.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa 40 lít. Kèm&nbsp;<strong>thang đo hiển thị mực nước</strong>&nbsp;tiện quan sát.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tính năng tự ngắt bơm khi cạn nước.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bảng điều khiển cảm ứng,&nbsp;<strong>cảm biến nhiệt độ phòng</strong>.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Có remote điều khiển từ xa tùy chọn chức năng dễ dàng.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Thương hiệu Sunhouse – Việt Nam, sản xuất tại Trung Quốc.</strong></span></li>\n</ul>', 1, 5, 22, '2021-01-19 10:25:19', NULL, NULL, NULL),
(638, 'Quạt điều hòa Sunhouse SHD7746', 4741000, 20, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tặng kèm 2 cục đá khô</strong>&nbsp;cho độ lạnh lâu hơn, làm mát hiệu quả hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Công suất mạnh mẽ&nbsp;<strong>180 W</strong>, dùng cho&nbsp;diện tích 40 m2.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Độ ồn từ&nbsp;60 dB – 70 dB&nbsp;phù hợp cho quán ăn, quán cafe.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Dung tích bình chứa nước<strong>&nbsp;45 lít</strong>, đáp ứng nhu cầu làm mát liên tục trong nhiều giờ, có cửa đổ nước rộng, dễ châm nước.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Sử dụng dễ dàng với bảng điều khiển nút nhấn có hướng dẫn tiếng Việt.&nbsp;<strong>Hẹn</strong>&nbsp;<strong>giờ tắt tối đa đến 7 tiếng.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa Sunhouse điều khiển từ xa nhỏ gọn với các phím chức năng có&nbsp;<strong>chỉ dẫn tiếng Việt dễ hiểu, thao tác đơn giản.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Vòi cấp nước trực tiếp liên tục</strong>, tiếp nước cho quạt tiện lợi hơn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Thương hiệu uy tín của Việt Nam – Sunhouse, sản xuất tại Trung Quốc.</span></li>\n</ul>', 1, 5, 22, '2021-01-19 10:25:23', NULL, NULL, NULL),
(639, 'Quạt điều hòa Sunhouse SHD7721', 4033000, 15, '<ul>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt điều hòa hoạt động mạnh mẽ với&nbsp;<strong>công suất 220 W.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Quạt có khả năng&nbsp;<strong>đảo chiều gió lên xuống, qua trái phải linh hoạt.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Độ ồn 70 – 80 dB.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Bầu không khí gia đình luôn trong lành</strong>, sạch sẽ với chức năng tạo ion lọc không khí.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Màng lọc,<strong>&nbsp;hạn chế được bụi bẩn từ không gian sử dụng.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Tính năng hong khô tấm làm mát sau khi máy ngưng hoạt động hẳn.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Bình chứa nước&nbsp;<strong>dung tích 30 lít,</strong>&nbsp;có thang đo hiển thị mực nước, chức năng hẹn giờ tắt với thời gian&nbsp;<strong>hẹn tối đa lên tới 24 tiếng.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Tự ngắt máy bơm khi cạn nước</strong>&nbsp;– Đảm bảo an toàn tối đa.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Bảng điều khiển nút nhấn chỉ dẫn tiếng Việt</strong>&nbsp;kèm màn hình hiển thị tiện theo dõi và thao tác.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\"><strong>Quạt có điều khiển từ xa nhỏ gọn</strong>, tiện dụng mọi địa điểm.</span></li>\n <li><span style=\"font-family: tahoma, arial, helvetica, sans-serif;\">Phía sau quạt điều hòa có trang bị<strong>&nbsp;2 cổng USB.</strong></span></li>\n <li><span style=\"font-family: tahoma, arial, hel